import React from 'react'
import { Modal, Button, Form, Col } from 'react-bootstrap'

export const PlanSettingsModal = ({ show, handleClose, handleChange, submitHandler, data, isLoading, isLoadingSettings }) => {
  return (
    show ?
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Plan settings</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {!isLoadingSettings ?
            <Form onSubmit={submitHandler}>
              <Form.Row>
                <Form.Group as={Col} controlId="timeBuffer">
                  <Form.Label>Time buffer (seconds)</Form.Label>
                  <Form.Control type="text" name="timeBuffer" value={data.timeBuffer} onChange={handleChange} />
                </Form.Group>
              </Form.Row>
              <Form.Row>
                <Form.Group as={Col} controlId="message">
                  <Form.Label>Custom message</Form.Label>
                  <Form.Control as="textarea" rows={5} name="message" value={data.message} onChange={handleChange} />
                </Form.Group>
              </Form.Row>
              <hr />
              <Form.Row className="justifyCenter">
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                &nbsp;
                <Button variant="primary" type="submit" disabled={isLoading}>
                  Update
                </Button>
              </Form.Row>
              {isLoading &&
                  <>
                    <span>Loading...</span><img alt="loading" src="data:image/gif;base64," />
                  </>
                }
            </Form>
            :
            <div className="loading-div">
              Loading settings... <img alt="loading" src="data:image/gif;base64," />
            </div>
          }

        </Modal.Body>
      </Modal>
      : null
  )
}